import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { DevTool } from '@hookform/devtools';
import axios from 'axios';
import Popup from 'reactjs-popup';
import './userForm.css'



const UserForm = () => {
    const serverURL = "http://localhost:8080"

    const form = useForm();
    const { register, control, handleSubmit, formState } = form;
    const { errors } = formState;


    // send user date to 
    const sendFormData = (data) => {
        console.log(data)
        const config = {
            headers: { 'Content-Type': 'multipart/form-data' }
        }
        if (data) {
            axios.post(serverURL + "/signupAccount", JSON.stringify(data), config).then((response) => {
                console.log("Form response: ", response.status)
            });
        }

    }

    const hideForm = () =>{

    }

    const login = () => {
        <Popup trigger={<button className='popup-button'>SIGN IN</button>}  >
        <div>
            <header className="header">LOG IN</header>
            <form className="userForm" onSubmit={handleSubmit(sendFormData)} noValidate> 
                <label>User name</label>
                <div className="form-control">
                    <input
                        type='text'
                        id = 'username'
                        placeholder='Enter username'
                        {...register("username", { 
                            required: 'Username is required', // validation
                        })}
                    />
                    <p className="error">{errors.username?.message}</p>
                </div>

                <div  className="form-control">
                    <label>Password</label>
                    <input
                        type='password'
                        id = 'password'
                        placeholder='Enter password'
                        {...register("password", {
                            required: 'Password is required',
                        })}
                    />
                    <p className="error">{errors.password?.message}</p>
                </div>
                <button type="submit">Submit</button>
            </form>

            <Popup trigger={<button className='popup-button'>REGISTER</button>}  >
            <div>
                <header className="header">REGISTER</header>
                <form className="userForm" onSubmit={handleSubmit(sendFormData)} noValidate> 
                    <label>User name</label>
                    <div className="form-control">
                        <input
                            type='text'
                            id = 'username'
                            placeholder='Enter username'
                            {...register("username", { 
                                required: 'Username is required', // validation
                            })}
                        />
                        <p className="error">{errors.username?.message}</p>
                    </div>

                    <div  className="form-control">
                        <label>Email</label>
                        <input
                            type='email'
                            id = 'email'
                            placeholder='Enter email'
                            {...register("email", {
                                required: 'Email is required', // validation
                                pattern: { // validation
                                    value: /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/,
                                    message: 'Invalid email format',
                                    validate: {
                                        notAdmin: (fieldValue) => {
                                            return (
                                                fieldValue !== "admin@chesswebsite.com" || "some other email address" // change depending on admin email
                                            );
                                        },
                                        notBlackListed: (fieldValue) => {
                                            return !fieldValue.endsWith("baddomain.com") || "This domain is not supported"
                                        }
                                    }
                                }
                            })}
                        />
                        <p className="error">{errors.email?.message}</p>
                    </div>

                    <div  className="form-control">
                        <label>Password</label>
                        <input
                            type='password'
                            id = 'password'
                            placeholder='Enter password'
                            {...register("password", {
                                required: 'Password is required',
                            })}
                        />
                        <p className="error">{errors.password?.message}</p>
                    </div>
                    <button type="submit">Submit</button>

                </form>
            </div>
        </Popup>
        </div>
    </Popup>
    }


    return (
        <Popup trigger={<button className='popup-button'>SIGN IN</button>} >
        <div>
            <header className="header">LOG IN</header>
            <form className="loginForm" onSubmit={handleSubmit(sendFormData)} noValidate> 
                <label>User name</label>
                <div className="form-control">
                    <input
                        type='text'
                        id = 'username'
                        placeholder='Enter username'
                        {...register("username", { 
                            required: 'Username is required', // validation
                        })}
                    />
                    <p className="error">{errors.username?.message}</p>
                </div>

                <div  className="form-control">
                    <label>Password</label>
                    <input
                        type='password'
                        id = 'password'
                        placeholder='Enter password'
                        {...register("password", {
                            required: 'Password is required',
                        })}
                    />
                    <p className="error">{errors.password?.message}</p>
                </div>
                <button type="submit">Submit</button>
            </form>

            <Popup trigger={<button className='popup-button' onClick={hideForm} >REGISTER</button>}  >
            <div>
                <header className="header">REGISTER</header>
                <form className="userForm" onSubmit={handleSubmit(sendFormData)} noValidate> 
                    <label>User name</label>
                    <div className="form-control">
                        <input
                            type='text'
                            id = 'username'
                            placeholder='Enter username'
                            {...register("username", { 
                                required: 'Username is required', // validation
                            })}
                        />
                        <p className="error">{errors.username?.message}</p>
                    </div>

                    <div  className="form-control">
                        <label>Email</label>
                        <input
                            type='email'
                            id = 'email'
                            placeholder='Enter email'
                            {...register("email", {
                                required: 'Email is required', // validation
                                pattern: { // validation
                                    value: /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/,
                                    message: 'Invalid email format',
                                    validate: {
                                        notAdmin: (fieldValue) => {
                                            return (
                                                fieldValue !== "admin@chesswebsite.com" || "some other email address" // change depending on admin email
                                            );
                                        },
                                        notBlackListed: (fieldValue) => {
                                            return !fieldValue.endsWith("baddomain.com") || "This domain is not supported"
                                        }
                                    }
                                }
                            })}
                        />
                        <p className="error">{errors.email?.message}</p>
                    </div>

                    <div  className="form-control">
                        <label>Password</label>
                        <input
                            type='password'
                            id = 'password'
                            placeholder='Enter password'
                            {...register("password", {
                                required: 'Password is required',
                            })}
                        />
                        <p className="error">{errors.password?.message}</p>
                    </div>
                    <button type="submit">Submit</button>

                </form>
            </div>
        </Popup>
        </div>
    </Popup>

    )
                        }

export default UserForm;
