import React from 'react';
import './board-styles.css';
import Cell from '../cell';

const Board = ({ cells }) => {
    return (
        <div className = "board">
            {cells.map((cell, index) =>(
                <Cell cell={cell} index={index} key={cell.pos} /> // here we create an array of cells
            ))}
        </div>
    );
};

export default Board;
