import { useForm } from 'react-hook-form';
import { DevTool } from '@hookform/devtools';
import axios from "axios";




const UserForm = () => {
    const serverURL = "http://localhost:8080"

    const form = useForm();
    const { register, control, handleSubmit } = form;



