            axios.post(serverURL + "/signupAccount", JSON.stringify(formData), config).then((response) => {
                console.log(response.data)
            }).then((data)=>{
                setFormData({username: '', email: '', password: ''}) // reset the form input box
            })
        }

    }


    return (

        <div>
            <form onSubmit={handleSubmit(sendFormData)}>
                <label>User name</label>
                <input
                    type='text'
                    id = 'username'
                    placeholder='Enter username'
                    {...register("username")}
                />

                <label>Email</label>
                <input
                    type='email'
                    id = 'email'
                    placeholder='Enter email'
                    {...register("email")}

                />
                <label>Password</label>
                <input
                    type='password'
                    id = 'password'
                    placeholder='Enter password'
                    {...register("password")}
                />
                <button type="submit">Submit</button>

            </form>
            <DevTool control={control}/>
        </div>
    )

}

export default UserForm;
