import React, { useState, useRef, useEffect, useContext} from 'react';
import './game.css';
import {Chess} from 'chess.js';
import { createBoard } from '../../functions/create-board';
import Board from  '../../components/board';
import { GameContext } from '../../context/GameContext';
import {     
    setMessage,
    setOpponent,
    setOpponentColour,
    setOpponentMoves,
    setPlayer,
    setPlayerColour,
    types, } from '../../context/actions';
import { getGameOverState } from '../../functions';
import  GameOver from '../../components/gameover';
import UserForm from '../../components/userform';

// import io from 'socket.io-client';
const serverURL = 'ws://localhost:8080/ws'



// const socket = io('localhost:8080/socket.io/', {
//     transports: ['websocket']
// });
const FEN = 'rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 1';
// checkmate for testing game over screen
// const FEN = 'rnb1kbnr/pppp1ppp/8/4p3/5PPq/8/PPPPP2P/RNBQKBNR w KQkq - 1 3';

const ws = new WebSocket(serverURL)



const Game = ()=> {
    const[fen, setFen] = useState(FEN);
    const {current: chess} = useRef(new Chess(fen));
    const [board,setBoard] = useState(createBoard(fen));
    const {gameOver} = useContext(GameContext);
    const { possibleMoves, dispatch, message } = useContext(GameContext);
    // const ws = new WebSocket("ws://localhost:8080/ws"


    useEffect(() => {
        ws.onopen = (event) =>{
            console.log("connection established: ", event)

            const message = {emit: "join", user : {name : "John"}}
            ws.send(JSON.stringify(message)) 
        }
        ws.onerror = (err) => {
            console.log(err)
        }

        ws.onmessage = (event) => {
            const msgReceived = JSON.parse(event.data)
            if (msgReceived.emit === "playerJoined"){
                console.log(msgReceived.message) //TODO: dispatch player to context
                console.log(msgReceived.name)
                dispatch(setPlayer(msgReceived.name))
                dispatch(setPlayerColour(msgReceived.colour))
            }
            if (msgReceived.emit === "opponentJoined"){
                console.log(msgReceived.message)
                console.log(msgReceived.name)
                console.log(msgReceived.colour)
                dispatch(setOpponent(msgReceived.name))
                dispatch(setOpponentColour(msgReceived.colour))



            }
            // ws.send(JSON.stringify({emit:"message", message: "msg received"}))
        }

        ws.onclose = (event) => {
            console.log("connection closed: ", event)
        }
    },[dispatch])
    // every time a change is made to the state of the game, the board is updated with the new fen
    useEffect(() =>{
        setBoard(createBoard(fen));
    }, [fen]);




    // listening for events from the server

//     useEffect(() => {
//         socket.on('connect_error', (error) => {
//             console.log('Websocket connection error: ', error)
//         });
            // socket.emit('join', { name: 'Frank', gameID: '20' }, ({ error, name, colour }) => {
//                 if (error){
//                     console.log(error)
//                 }
//                 dispatch(setPlayer(name));
//                 dispatch(setPlayerColour(colour))
//             });


//         socket.on('welcome', ({ message, opponent }) => {
//             dispatch(setMessage(message));
//             dispatch(setOpponent(opponent));
//         });
//         socket.on('opponentJoin', ({ message, opponent }) => {
//             dispatch(setMessage(message));
//             dispatch(setOpponent(opponent));
//             dispatch(setOpponentColour(opponent.colour));
//         });
//         socket.on('opponentLeft', ({ message }) => {
//             console.log(message)
//         });

//         socket.on('opponentMove', ({ from, to }) => {
//             chess.move({ from, to });
//             setFen(chess.fen());
//             dispatch(setMessage('Your Turn'));
//             dispatch(setOpponentMoves([from, to]));
//         });
//         socket.on('message',({ message })=> {
//             console.log(message)
//         });

// }, [chess, dispatch]);


    // will detect if a player is in check
    useEffect(() =>{
        const [gameOver, status] = getGameOverState(chess);
        if (gameOver) {
            dispatch({ type: types.GAME_OVER, status, player: chess.turn() });
            return
        }
        dispatch({
            type: types.SET_TURN,
            player: chess.turn(),
            check: chess.isCheck(),
        });
    }, [fen, dispatch, chess]);

    /** move handling */
    const fromPos = useRef(); /** follow setFromPos to the piece component */

    // share move set with components

    const makeMove = (pos) =>{
        const from = fromPos.current;
        const to = pos;

        var validMove = possibleMoves.includes(to)
         if (validMove){
            chess.move({ from, to });
            // socket.emit('move', { gameID: '20', from, to}) // send moves to the other player through the websocket
            dispatch({ type: types.CLEAR_POSSIBLE_MOVES}) // unhighlight possible moves
            setFen(chess.fen()); // update the fen with the new move/piece positions
        }
    };

    // this is called once a piece is dragged
    const setFromPos = (pos) => {
        fromPos.current = pos
        dispatch({
            type:types.SET_POSSIBLE_MOVES,
            moves: chess.moves({ square: pos }) // send the possible moves from the currently selected position to highlight
        })
    };

    // if (gameOver) {
    //     return <GameOver />
    // };

    return (
            <div className="game">
            <Board cells = {board} makeMove={makeMove} setFromPos={setFromPos}/>
            </div>
    );

};

export default Game;
