import React, {useState} from 'react';
import LoginForm from '../loginForm'

const UserForm = () => {
    const [click, setClick] = useState(false)

    const togglePop = () =>{
        setClick(!click);
    };

    return (
        <div>
            <button onClick={togglePop}>SIGN IN</button>
            {click ? <LoginForm toggle={togglePop}/> : null}
        </div>
    )
}

export default UserForm
